showTips:let query = "%s";
let url = `https://www.imdb.com/find/?q=${query}&exact=true`;
let geneHTML = (title, intro, labels, director, writer, stars, image, rating, year) => {
if (!title) return "No result";
return `
<p style="margin: 5px;">
${title}
<span style="position: absolute; right: 10px; color: orange;">${rating}</span>
</p>
<div style="display: flex; font-size: 20px; width: 500px;">
<img style="height: fit-content;" src="${image}"/>
<div style="font-size: 16px; line-height: 1.5; text-align: left; margin: 5px;">
<div>${labels.reduce((r, c) => `${r}<span style="white-space: nowrap;margin: 5px; font-size: 16px; border-radius: 5px; padding: 2px; box-shadow: 0px 0px 10px 0px #000;">${c}</span>`, "")}</div>
<div>Year: ${year}</div>
<div>Director: ${director}</div>
<div>Writer: ${writer}</div>
<div>Stars: ${stars}</div>
<div style="font-size: 16px; margin-top: 10px; border-top: 1px solid;">${intro}</div>
</div>
</div>`;};
let imdbData = await storage.getItem("imdbData");
if (!imdbData) imdbData=[];
let d = imdbData.find(data => data.query == query);
if (d) {
d = d.data;
return[geneHTML(d.title, d.intro, d.labels, d.director, d.writer, d.stars, d.image, d.rating, d.year), url];
}
let result = await fetch(url).then(r => r.text()).then(async r => {
let doc = document.implementation.createHTMLDocument(''); doc.documentElement.innerHTML = r;
let item = doc.querySelector('.find-title-result .ipc-metadata-list-summary-item__t');
if (!item) return null;
let _result = {title: item.innerText};
url = "https://www.imdb.com/" + item.getAttribute("href");
await fetch(url).then(r => r.text()).then(r => {
let doc = document.implementation.createHTMLDocument(''); doc.documentElement.innerHTML = r;
let img = doc.querySelector(".ipc-image");
if (img) _result.image = img.src;
_result.year = doc.querySelector("h1+ul>li>.ipc-link").innerText;
_result.intro = doc.querySelector("section>p>span").innerText;
_result.labels = [];
[].forEach.call(doc.querySelectorAll("a.ipc-chip"), ele => {
_result.labels.push(ele.innerText)
});
_result.director = [];
[].forEach.call(doc.querySelectorAll("section>div>div>.title-pc-list>li:nth-child(1) li"), ele => {
_result.director.push(ele.innerText)
});
_result.director = _result.director.join("/");
_result.writer = [];
[].forEach.call(doc.querySelectorAll("section>div>div>.title-pc-list>li:nth-child(2) li"), ele => {
_result.writer.push(ele.innerText)
});
_result.writer = _result.writer.join("/");
_result.stars = [];
[].forEach.call(doc.querySelectorAll("section>div>div>.title-pc-list>li:nth-child(3) li"), ele => {
_result.stars.push(ele.innerText)
});
_result.stars = _result.stars.join("/");
_result.rating = doc.querySelector(".ipc-btn__text>div>div>div").innerText;
});
return _result;
}).catch(alert);
if (!result) result = {};
imdbData.push({query:query, data:result});
if (imdbData.length > 50) imdbData.shift();
storage.setItem("imdbData", imdbData);
d = result;
return[geneHTML(d.title, d.intro, d.labels, d.director, d.writer, d.stars, d.image, d.rating, d.year), url];
